module Quests where

go :: Int -> Char -> Int
go f '(' = f + 1
go f ')' = f - 1
go _ _   = error "Wrong char"

quest01 :: String -> Int
quest01 = foldl (go) 0

quest02 :: String -> Int -> Int -> Int
quest02 []     p f = p
quest02 (x:xs) p f = if nf < 0 then
                       p
                     else
                       quest02 xs (p+1) nf
                     where
                       nf = go f x


























